data.value:
select t_span.*
from (
	select t_sorted.*
	from (
		select $(column)
		from $(schema).$(table)
		order by $(column)
	) t_sorted
	offset @offset
) t_span
limit 1

select t_span.*
from (
	select t_sorted.*
	from (
		select $(column-list)
		from $(schema).$(table)
		order by $(watermark-column)
	) t_sorted	
	limit @limit offset @offset	
) t_span
